// page init
jQuery(function() {
	//initRedirectOnLogin();
	initYoutubeVideo();
	//initInVieport();
	//initCycleCarousel();
	initStickyNavbar();
	initMobileNav();
	//initLightbox();
	initSlickCarousel();
	//initSameHeight();
	initFitVids();
	//initOpenClose();
	//initAnchors();

	initPcturefill();

										/* My scripts start */
	/* Chrome Smooth Scroll */
	try {
		$.browserSelector();
		if($("html").hasClass("chrome")) {
			$.smoothScroll();
		}
	} catch(err) {};


	/* Prevent Drag for a, img */
	$("img, a").on("dragstart", function(event) { event.preventDefault(); });


	/* Scroll page to top */
	$(window).scroll(function () {
		if ($(this).scrollTop() > $(this).height()) {
			$('.top').addClass('active');
		} else {
			$('.top').removeClass('active');
		}
	});
	$('.top').click(function () {
		$('html, body').stop().animate({scrollTop: 0}, 'slow', 'swing');
	});


	/* Preloader */
	var hellopreloader = document.getElementById("hellopreloader_preload");
	function fadeOutnojquery(el){
		el.style.opacity = 1;
		var interhellopreloader = setInterval(function(){
			el.style.opacity = el.style.opacity - 0.05;
			if (el.style.opacity <=0.05){
				clearInterval(interhellopreloader);
				hellopreloader.style.display = "none";}
		},16);
	}
	window.onload = function(){
		setTimeout(function(){
			fadeOutnojquery(hellopreloader);
		},1000);
	};


	/* iframe */

	$(".video__start").click(function() {
		$("#iframe").css('z-index', '5');
		$("#iframe")[0].src = "https://www.youtube.com/embed/f77SKdyn-1Y?autoplay=1";
	});

	/* Height depend of width */

	resizeHeight();
	$(window).resize(function() {
		resizeHeight();
	});

	function resizeHeight() {
		var block = $('.header');
		var blockWidth = block.width();
		block.css({'height': blockWidth/1.36 + "px"});
		block.css({'max-height': 768 + "px"});
		block.css({'min-height': 540 + "px"});
	}
									/* My scripts end */
});

								  /* Init plugins start */

function initPcturefill() {
	picturefill({
		elements: [ document.getElementById( "logo-picturefill" ) ]
	});
};

function initRedirectOnLogin() {
	jQuery('#pt_login_form').each(function() {
		var $form = jQuery(this);

		function ajaxSuccessHandler(e, xhr) {
			var obj = getJson(xhr.responseText);
			var redirectUrl = window.pathInfo.url_var;

			if ('error' in obj && !obj.error && redirectUrl) {
				window.location.href = redirectUrl;
			}
		}

		$form.ajaxSuccess(ajaxSuccessHandler);
	});

	function getJson(str) {
		try {
			return JSON.parse(str);
		} catch (err) {
			return {};
		}
	}
}

function initYoutubeVideo() {
	jQuery('iframe').each(function() {
		var src = this.src;

		if (src.indexOf('youtube') !== -1) {
			this.src = src + (src.indexOf('?') !== - 1 ? '&' : '?') + 'enablejsapi=1';
		}
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: '.to-contact',
		activeClasses: 'parent',
		anchorActiveClass: 'active'
	});
}

// open-close init
function initOpenClose() {
	jQuery('.case-holder').openClose({
		activeClass: 'active',
		opener: '.case-opener',
		slider: '.video-slide',
		animSpeed: 400,
		effect: 'slide',
		onBeforeHide: function() {
			this.holder.find('iframe').each(function() {
				this.contentWindow.postMessage('{"event":"command","func":"' + 'pauseVideo' + '","args":""}', '*');
			});
		}
	});
}

// handle flexible video size
function initFitVids() {
	jQuery('body').fitVids();
}

function initSlickCarousel() {
	jQuery('.portf-slick').slick({
		dots: true,
		infinite: true,
		speed: 300,
		slidesToShow: 1,
		slidesToScroll: 1,
		responsive: true
	});

	jQuery('.reviews').slick({
		dots: true,
		prevArrow: false,
		nextArrow: false,
		infinite: true,
		speed: 300,
		autoplay: false,
		slidesToShow: 1,
		slidesToScroll: 1,
		//vertical: true,
		//verticalSwiping: true
	});
}

// cycle scroll gallery init
function initCycleCarousel() {
	jQuery('div.gallery').scrollAbsoluteGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		stretchSlideToMask: true,
		pauseOnHover: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('.top-line').mobileNav({
		//hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener'
	});
}

// block in viewport init
function initInVieport() {
	jQuery('[data-animate]').itemInViewport({
		activeClass: 'in-viewport',
		once: true
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(0, 0, 0, 0.5)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		}
	});
}

// init sticky navbar
function initStickyNavbar() {
	//jQuery('.header:not(".members-area .header")').stickyNavbar({
	jQuery('.top-line').stickyNavbar({
		onSticky: refreshNavigation,
		offSticky: refreshNavigation
	});

	function refreshNavigation(instance) {
		instance.menu = instance.holder.find('.slide');
		instance.menuClone = instance.holderClone.find('.slide');
	}
}

